INSERT INTO `content` VALUES (8,'bannerHeader','content','WaxLuna Games','2022-09-29 02:27:13','2022-10-30 18:33:54'),(9,'bannerSubHeader','content','Home of POWERS Role Playing System','2022-09-29 02:27:13','2022-10-30 18:34:18'),(10,'banner','content','<div>Publishers of such fine games as POWERS and all it\'s related settings.&nbsp; POWERS is a super human rpg system that allows you to play in any of several settings, even your own!&nbsp; Take a dive into the default campaign setting, the world of InfiniteOblivion.<br><br>Photo by <a href=\"https://unsplash.com/@nika_benedictova?utm_source=unsplash&amp;utm_medium=referral&amp;utm_content=creditCopyText\">Nika Benedictova</a> on <a href=\"https://unsplash.com/s/photos/gaming-dice?utm_source=unsplash&amp;utm_medium=referral&amp;utm_content=creditCopyText\">Unsplash</a></div>','2022-09-29 02:27:13','2022-09-29 03:55:37'),(11,'getInTouch','content','Want to learn more?  Interested in writing for us?  Fill out the form and let us know.','2022-09-29 03:19:12','2022-11-02 21:49:13'),(12,'contactEmail','content','<div>JasonMarshallSelene@gmail.com</div>','2022-09-30 02:00:57','2022-09-30 02:01:17'),(13,'learnMore','content','Learn More','2022-09-30 02:38:40','2022-09-30 02:38:40'),(14,'tracking','content','','2022-10-02 14:30:55','2022-10-02 14:30:55'),(17,'matomo','content','<!-- Matomo -->\r\n<script>\r\n  var _paq = window._paq = window._paq || [];\r\n  /* tracker methods like \"setCustomDimension\" should be called before \"trackPageView\" */\r\n  _paq.push([\"setDomains\", [\"*.waxlunagames.com\"]]);\r\n  _paq.push([\'trackPageView\']);\r\n  _paq.push([\'enableLinkTracking\']);\r\n  (function() {\r\n    var u=\"//matomo.waxlunagames.com/\";\r\n    _paq.push([\'setTrackerUrl\', u+\'matomo.php\']);\r\n    _paq.push([\'setSiteId\', \'1\']);\r\n    var d=document, g=d.createElement(\'script\'), s=d.getElementsByTagName(\'script\')[0];\r\n    g.async=true; g.src=u+\'matomo.js\'; s.parentNode.insertBefore(g,s);\r\n  })();\r\n</script>\r\n<!-- End Matomo Code -->','2022-10-16 16:37:06','2022-10-18 00:47:53'),(18,'metaDescription','content','Tabletop games publisher, maker of Powers RPG.','2022-10-19 21:51:15','2022-10-19 22:07:10'),(19,'metaKeywords','content','tabletop, rpg, role play game, game, powers rpg','2022-10-28 12:35:01','2022-10-28 12:35:01'),(20,'contactForm','content','                    <form action=\"https://formspree.io/f/mpznynly\" method=\"POST\">\n                        <label>\n                            Your email:\n                            <input type=\"email\" name=\"email\">\n                        </label>\n                        <label>\n                            Your message:\n                            <textarea name=\"message\"></textarea>\n                        </label>\n                        <!-- your other form fields go here -->\n                        <button type=\"submit\">Send</button>\n                    </form>\n                    ','2022-10-30 18:35:33','2022-10-30 18:35:33');
